`,(0,l.jsx)(s,{name:"bubble-list-demo",description:"Preset Bubble list. Support auto scroll. Use roles to set default properties of Bubble",children:(0,l.jsx)(e.div,{"data-rehype-pretty-code-fragment":"",children:(0,l.jsx)(e.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`import * as React from "react"
import { Bot } from "lucide-react"

import type { BubbleListProps, BubbleListRef } from '@/registry/aoian-ui/bubble/types'
import {
  BubbleAvatar,
} from "@/registry/aoian-ui/bubble/bubble"
import { BubbleList } from '@/registry/aoian-ui/bubble/bubble-list'
import { Button } from "@/components/ui/button"

const roles: BubbleListProps["roles"] = {
  ai: {
    placement: "start",
    typing: { step: 5, interval: 20 },
    className: "max-w-[600px]",
    shape: "corner",
    avatar: (
      <BubbleAvatar>
        <Bot size={18} />
      </BubbleAvatar>
    ),
  },
  user: {
    placement: "end",
    shape: "corner",
    avatar: (
      <BubbleAvatar
        src="https://avatars.githubusercontent.com/u/9461149?s=48&v=4"
        alt="@petitspois"
      />
    ),
  },
}

export function BubbleListDemo() {
  const [count, setCount] = React.useState(3)
  const listRef = React.useRef<BubbleListRef>(null)
  return (
    <div className="grow space-y-4 self-start">
      <div className="space-x-2">
        <Button
          onClick={() => {
            setCount((i) => i + 1)
          }}
        >
          Add Bubble
        </Button>
        <Button
          onClick={() => {
            listRef.current?.scrollTo({ key: 0, block: "nearest" })
          }}
        >
          Scroll To First
        </Button>
      </div>
      <BubbleList
        ref={listRef}
        className="max-h-[300px]"
        roles={roles}
        items={Array.from({ length: count }).map((_, i) => {
          const isAI = !!(i % 2)
          const content = isAI
            ? "Mock AI content. ".repeat(20)
            : "Mock user content."

          return {
            key: i,
            role: isAI ? "ai" : "user",
            content,
          }
        })}
      />
    </div>
  )
}
