import {
  ArrowLeft,
  ArrowRight,
  RotateCw,
  X,
  ZoomIn,
  ZoomOut,
} from "lucide-react"
import { PhotoProvider, PhotoView } from "react-photo-view"

import { Button } from "@/components/ui/button"
import { cn } from "@/registry/lib/utils"

// align-items: center;
